    public void mudar(String end){
        Image troca = Toolkit.getDefaultToolkit().getImage(getClass().getResource(end));
        
        ImageIcon icone = new ImageIcon(troca);
        
        jImagem.setIcon(icone);
        
    }
    
    private javax.swing.Timer cordenadas = new javax.swing.Timer(600, new java.awt.event.ActionListener() { 
              public void actionPerformed(java.awt.event.ActionEvent e) {

                  x = 1 + (int) (600 * Math.random());
                  y = 1 + (int) (400 * Math.random());
                  
                  jImagem.setLocation(x,y);
                     
   }//fim do evento
});//fim m√©todo timer

